const cloneDeep=require("lodash.clonedeep");function assign(e={},n={},o=!0){if("object"!=typeof e){if(o)throw new Error("Assign Function: The target provided is not an object");return null}if("object"!=typeof n){if(o)throw new Error("Assign Function: The source provided is not an object");return null}try{return Object.assign(cloneDeep(e),cloneDeep(n))}catch(e){if(o)throw e}}module.exports=assign;