const NF=require("node-forge"),base64From=require("./base64From");function messageDecryptFromChunks(e,r){if(!e||!Array.isArray(e)||0==e.length)return"";if(!r||!r.includes("-----BEGIN PRIVATE KEY-----")||!r.includes("-----END PRIVATE KEY-----"))throw new Error("Public Key is not well PEM formatted");const o=NF.pki.privateKeyFromPem(r),s=[];for(let r of e)s.push(o.decrypt(base64From(r),"RSA-OAEP",{md:NF.md.sha256.create()}));return s.join("")}module.exports=messageDecryptFromChunks;