const NF=require("node-forge");function messageDecryptFromChunks(e,r){if(!e||!Array.isArray(e)||0==e.length)return"";if(!r||!r.includes("-----BEGIN PRIVATE KEY-----")||!r.includes("-----END PRIVATE KEY-----"))throw new Error("Private Key is not well PEM formatted");const o=NF.pki.privateKeyFromPem(r),t=[];for(let r of e)t.push(o.decrypt(NF.util.decode64(r),"RSA-OAEP"));return t.join("")}module.exports=messageDecryptFromChunks;