const toString=require("./toString.js"),isObject=require("../helpers/isObject");function removeDuplicatedStrings(t,e=" ",r=!1){if(isObject(t))return"";const i=toString(t).trim().split(toString(e));if(!r)return[...new Set(i)].filter((t=>t)).join(e);const n={};for(let t in i)n[i[t].toLowerCase()]=t;const o=Object.values(n);return i.filter(((t,e)=>t&&o.includes(e.toString()))).join(e)}module.exports=removeDuplicatedStrings;